import React, { useEffect, useState } from "react";
import Header from "../../components/Header";
import {
  Container,
  Row,
  Col,
  Card
} from "react-bootstrap";
import API from "../../api";

function StudentDashboard() {

  const [student, setStudent] = useState(null);
  const [bus, setBus] = useState(null);
  const [driver, setDriver] = useState(null);

  /* ================= FETCH ================= */

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const res = await API.get("/student/profile");
      setStudent(res.data.student);
      setDriver(res.data.driver);
      setBus(res.data.bus);
    } catch (err) {
      console.error("Error fetching student data:", err);
    }
  };

  /* ================= AUTO REFRESH ================= */

  useEffect(() => {
    const interval = setInterval(() => {
      setStudent(prev => ({ ...prev }));
    }, 60000);

    return () => clearInterval(interval);
  }, []);

  /* ================= TRACKING ================= */
  let c



  /* ================= UI ================= */

  return (
    <div>
      <Header />

      <Container className="mt-4">

        <h3>Welcome, {student?.name}</h3>

        <Row>
          <Col md={6}>
            <Card className="mt-4 shadow p-3">
              <Card.Body>
                <h5>Student Details</h5>
                <hr />
                <p><strong>Name:</strong> {student?.name}</p>
                <p><strong>Roll:</strong> {student?.rollNumber}</p>
                <p><strong>Department:</strong> {student?.department}</p>
                <p><strong>Year:</strong> {student?.year} year</p>
                <p><strong>Contact:</strong> {student?.phone}</p>
              </Card.Body>
            </Card>
          </Col>

          <Col md={6}>
            <Card className="mt-4 shadow p-3">
              <Card.Body>
                <h5>Bus Details</h5>
                <hr />
                <p><strong>Assigned Bus:</strong> {student?.assignedBus}</p>
                <p><strong>Bus Stop:</strong> {student?.busStop}</p>
                <p><strong>Timing:</strong> {stopTime}</p>
                <p><strong>Driver:</strong> {driver?.name || "Not Assigned"}</p>
                <p><strong>Contact:</strong> {driver?.phone || "-"}</p>
              </Card.Body>
            </Card>
          </Col>
        </Row>

        {/* ================= ROUTE PROGRESS ================= */}






       <Card className="mt-4 shadow p-3">
        <Card.Body>
          <h5>Where is my Bus</h5>
          <hr/>
          {bus ? (
            <>
            <p>{student?.assignedBus} is reached your destination with in </p>


            </>
          ) : (
            <>
            <p>No bus assigned yet.</p>
            </>
          )}

        </Card.Body>
       </Card>















        <Card className="mt-4 shadow p-3">
          <Card.Body>
            <h5>Announcements</h5>
            <hr />
            <p>No new announcements</p>
          </Card.Body>
        </Card>

      </Container>
    </div>
  );
}

export default StudentDashboard;
