import React, { useEffect, useState } from "react";
import Header from "../../components/Header";
import {
  Badge,
  Card,
  CardBody,
  Col,
  Form,
  Row,
  Button,
  Tabs,
  Tab,
  Table,
  Modal
} from "react-bootstrap";
import API from "../../api";

function DriverDashboard() {
  const [key, setKey] = useState("apply");

  const [driver, setDriver] = useState(null);
  const [bus, setBus] = useState(null);
  const [tripData, setTripData] = useState(null);

  const [showTracking, setShowTracking] = useState(false);
  const [loadingTrip, setLoadingTrip] = useState(false);

  const [rating, setRating] = useState(0);
  const [feedbackModal, setFeedbackModal] = useState(false);

  /* ===========================
     FETCH DRIVER PROFILE
  ============================ */
  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const res = await API.get("/driver/profile");
        setDriver(res.data.driver);
        setBus(res.data.bus);
      } catch (err) {
        console.error("Profile error:", err);
      }
    };

    fetchProfile();
  }, []);

  /* ===========================
     FETCH TRIP ETA
  ============================ */
  const fetchTripETA = async () => {
    if (!bus?.busNo) return;

    try {
      setLoadingTrip(true);
      const res = await API.get(`/driver/trip/${bus.busNo}/eta`);
      setTripData(res.data);
    } catch (err) {
      console.error("Trip ETA error:", err);
    } finally {
      setLoadingTrip(false);
    }
  };

  /* ===========================
     AUTO REFRESH (10s)
  ============================ */
  useEffect(() => {
    let interval;

    if (showTracking && bus?.busNo) {
      fetchTripETA();
      interval = setInterval(fetchTripETA, 10000);
    }

    return () => clearInterval(interval);
  }, [showTracking, bus]);

  /* ===========================
     TIMELINE DATA
  ============================ */
  const stops = tripData?.eta || [];
  const progress = tripData?.progressPercent || 0;

  return (
    <div>
      <Header />

      <div className="container mt-4">
        <h3>Welcome, {driver?.name || "Driver"} ðŸ‘‹</h3>

        <Row>
          {/* ================= DRIVER DETAILS ================= */}
          <Col md={6} className="mt-4">
            <Card className="p-3 shadow">
              <CardBody>
                <h5>Driver Details</h5>
                <hr />
                <p><strong>Name:</strong> {driver?.name}</p>
                <p><strong>Driver ID:</strong> {driver?.driverId}</p>
                <p><strong>Phone:</strong> {driver?.phone}</p>
                <p>
                  Duty Assigned:{" "}
                  <Badge bg={bus ? "success" : "secondary"}>
                    {bus ? "Yes" : "No"}
                  </Badge>
                </p>
              </CardBody>
            </Card>

            {/* ================= BUS DETAILS ================= */}
            <Card className="shadow mt-4 p-3">
              <CardBody>
                <h5>Bus Details</h5>
                <hr />
                <p><strong>Bus No:</strong> {bus?.busNo}</p>
                <p><strong>Route:</strong> {bus?.routeName}</p>
                <p><strong>Morning:</strong> {bus?.tripStartMorning} - {bus?.tripEndMorning}</p>
                <p><strong>Evening:</strong> {bus?.tripStartEvening} - {bus?.tripEndEvening}</p>
              </CardBody>
            </Card>
          </Col>

          {/* ================= RIGHT SIDE ================= */}
          <Col md={6} className="mt-4">
            <Card className="shadow p-3">
              <CardBody>
                <h5>Trip Control</h5>
                <hr />
                <Button
                  variant="primary"
                  onClick={() => setShowTracking(true)}
                >
                  View Live Tracking
                </Button>
              </CardBody>
            </Card>
          </Col>
        </Row>
      </div>

      {/* ================= TRACKING MODAL ================= */}
      <Modal
        show={showTracking}
        onHide={() => setShowTracking(false)}
        size="lg"
        centered
      >
        <Modal.Header closeButton>
          <Modal.Title>Live Bus Tracking</Modal.Title>
        </Modal.Header>

        <Modal.Body>
          {loadingTrip ? (
            <p>Loading trip data...</p>
          ) : !tripData || tripData.message === "No active trip" ? (
            <p>No active trip currently.</p>
          ) : (
            <>
              {/* Trip Status */}
              <div className="mb-3">
                <strong>Status:</strong>{" "}
                <Badge
                  bg={
                    tripData.status === "running"
                      ? "success"
                      : tripData.status === "paused"
                      ? "warning"
                      : "secondary"
                  }
                >
                  {tripData.status}
                </Badge>
                <br />
                <strong>Current Stop:</strong>{" "}
                {tripData.currentStop?.stopName}
                <br />
                <strong>Next Stop:</strong>{" "}
                {tripData.nextStop?.stopName || "Final Stop"}
              </div>

              {/* Progress Bar */}
              <div className="progress mb-4">
                <div
                  className="progress-bar bg-success"
                  style={{ width: `${progress}%` }}
                >
                  {Math.round(progress)}%
                </div>
              </div>

              {/* Timeline */}
              {stops.map((item, index) => (
                <div
                  key={index}
                  style={{
                    display: "flex",
                    alignItems: "center",
                    marginBottom: "15px"
                  }}
                >
                  <div style={{ width: "120px" }}>
                    {new Date(item.eta).toLocaleTimeString([], {
                      hour: "2-digit",
                      minute: "2-digit"
                    })}
                  </div>

                  <div
                    style={{
                      width: "20px",
                      height: "20px",
                      borderRadius: "50%",
                      backgroundColor:
                        item.status === "completed"
                          ? "green"
                          : item.status === "current"
                          ? "orange"
                          : "#ccc",
                      marginRight: "10px"
                    }}
                  ></div>

                  <div>{item.stop?.stopName}</div>
                </div>
              ))}
            </>
          )}
        </Modal.Body>
      </Modal>
    </div>
  );
}

export default DriverDashboard;
