import React, { useEffect, useState } from "react";
import Header from "../../components/Header";
import {
  Badge,
  Card,
  CardBody,
  Col,
  Row,
  Button,
  Modal,
  ProgressBar
} from "react-bootstrap";
import API from "../../api";

function DriverDashboard() {
  const [driver, setDriver] = useState(null);
  const [bus, setBus] = useState(null);
  const [tripData, setTripData] = useState(null);
  const [announcements, setAnnouncements] = useState([]);

  const [showTracking, setShowTracking] = useState(false);
  const [loadingTrip, setLoadingTrip] = useState(false);

  /* ================= PROFILE ================= */
  useEffect(() => {
    const fetchProfile = async () => {
      const res = await API.get("/driver/profile");
      setDriver(res.data.driver);
      setBus(res.data.bus);
    };

    const fetchAnnouncements = async () => {
      const res = await API.get("/driver/announcement");
      setAnnouncements(res.data);
    };

    fetchProfile();
    fetchAnnouncements();
  }, []);

  /* ================= TRIP ETA ================= */
  const fetchTripETA = async () => {
    if (!bus?.busNo) return;

    setLoadingTrip(true);
    const res = await API.get(`/driver/trip/${bus.busNo}/eta`);
    setTripData(res.data);
    setLoadingTrip(false);
  };

  useEffect(() => {
    let interval;
    if (showTracking && bus?.busNo) {
      fetchTripETA();
      interval = setInterval(fetchTripETA, 10000);
    }
    return () => clearInterval(interval);
  }, [showTracking, bus]);

  return (
    <div>
      <Header />
      <div className="container mt-4">
        <h3>Welcome, {driver?.name}</h3>

        <Row>
          {/* LEFT SIDE */}
          <Col md={6}>
            <Card className="shadow p-3">
              <CardBody>
                <h5>Driver Details</h5>
                <hr />
                <p><strong>Name:</strong> {driver?.name}</p>
                <p><strong>ID:</strong> {driver?.driverId}</p>
                <p><strong>Phone:</strong> {driver?.phone}</p>
                <p>
                  Duty Assigned:{" "}
                  <Badge bg={bus ? "success" : "secondary"}>
                    {bus ? "Yes" : "No"}
                  </Badge>
                </p>
              </CardBody>
            </Card>

            <Card className="shadow mt-4 p-3">
              <CardBody>
                <h5>Bus Details</h5>
                <hr />
                <p><strong>Bus No:</strong> {bus?.busNo}</p>
                <p><strong>Route:</strong> {bus?.routeName}</p>
                <p>
                  <strong>Morning:</strong>{" "}
                  {bus?.tripStartMorning} - {bus?.tripEndMorning}
                </p>
                <p>
                  <strong>Evening:</strong>{" "}
                  {bus?.tripStartEvening} - {bus?.tripEndEvening}
                </p>
              </CardBody>
            </Card>
          </Col>

          {/* RIGHT SIDE */}
          <Col md={6}>
            <Card className="shadow p-3">
              <CardBody>
                <h5>Announcements</h5>
                <hr />
                {announcements.length === 0 ? (
                  <p>No announcements</p>
                ) : (
                  announcements.map((a, i) => (
                    <div key={i} className="mb-2">
                      <strong>{a.title}</strong>
                      <p>{a.message}</p>
                      <hr />
                    </div>
                  ))
                )}
              </CardBody>
            </Card>

            <Card className="shadow mt-4 p-3">
              <CardBody>
                <Button onClick={() => setShowTracking(true)}>
                  View Live Tracking
                </Button>
              </CardBody>
            </Card>
          </Col>
        </Row>
      </div>

      {/* TRACKING MODAL */}
      <Modal show={showTracking} onHide={() => setShowTracking(false)} size="lg" centered>
        <Modal.Header closeButton>
          <Modal.Title>Live Bus Tracking</Modal.Title>
        </Modal.Header>

        <Modal.Body>
          {loadingTrip ? (
            <p>Loading...</p>
          ) : !tripData || tripData.message === "No active trip" ? (
            <p>No active trip.</p>
          ) : (
            <>
              <Badge bg="info">{tripData.status}</Badge>
              <ProgressBar
                now={tripData.progressPercent}
                label={`${Math.round(tripData.progressPercent)}%`}
                className="my-3"
              />

              {tripData.eta.map((item, index) => (
                <div key={index} style={{ display: "flex", marginBottom: "10px" }}>
                  <div style={{ width: "100px" }}>
                    {new Date(item.eta).toLocaleTimeString([], {
                      hour: "2-digit",
                      minute: "2-digit"
                    })}
                  </div>

                  <div
                    style={{
                      width: 15,
                      height: 15,
                      borderRadius: "50%",
                      backgroundColor:
                        item.status === "completed"
                          ? "green"
                          : item.status === "current"
                          ? "orange"
                          : "#ccc",
                      marginRight: 10
                    }}
                  ></div>

                  <div>{item.stop?.stopName}</div>
                </div>
              ))}
            </>
          )}
        </Modal.Body>
      </Modal>
    </div>
  );
}

export default DriverDashboard;
